document.addEventListener("DOMContentLoaded",(()=>{document.querySelectorAll('a[href^="#"]').forEach((e=>{e.addEventListener("click",(t=>{t.preventDefault();const o=document.querySelector(e.getAttribute("href"));let n=100;window.innerWidth<=950&&(n=50),o&&window.scrollTo({top:o.offsetTop-n,behavior:"smooth"})}))}))}));